#include "Newton.h"

Newton::Newton() {

}

Newton::~Newton() {

}

long double Newton::newton(int n, int k, Factorial* factorialPtr) {
    int N = n - k;
    int NbyK = 1;

    if(k >= N) {
        for(int i=k+1; i<=n; i++){
            NbyK *= i;
        }
        return NbyK/factorialPtr->factorial(N);
    } else {
        for(int i = N+1; i <= n; i++) {
            NbyK *= i;
        }
        return NbyK/factorialPtr->factorial(k);
    }
}

long double Newton::newtonStandard(int n, int k, Factorial* factorialPtr) {
    return factorialPtr->factorial(n) / (factorialPtr->factorial(k) * factorialPtr->factorial(n-k));
}

